function initMap(){var myLatlng={lat:46.82529138134654,lng:1.0692857499999908};var map=new google.maps.Map(document.getElementById('map'),{zoom:4,center:myLatlng});var infoWindow=new google.maps.InfoWindow({content:'Click the map to get Lat/Lng!',position:myLatlng});infoWindow.open(map);map.addListener('click',function(mapsMouseEvent){infoWindow.close();infoWindow=new google.maps.InfoWindow({position:mapsMouseEvent.latLng});infoWindow.setContent(mapsMouseEvent.latLng.toString());infoWindow.open(map)})}